import React from "react";
import { useFetch } from "./components/hooks/useFetch";
import "./styles/settings.scss";

export const MainApp = () => {
  const state = useFetch();
  const imgSize = "cover_big";
  console.log(state);

  return (
    <>
      <h1>Main app</h1>
      {state.map((item) => (
        <div id="carouselExampleControls" className="carousel slide" data-ride="carousel" key={item.id}>
          <div className="carousel-inner">
            <h1 className="carousel-item active">{item.name}</h1>
            <p className="carousel-item active">{item.summary}</p>
            <img className="carousel-item active"
              src={`https://images.igdb.com/igdb/image/upload/t_${imgSize}/${item.cover.image_id}.jpg`}
              alt=""
            />
          </div>
        </div>
      ))}
    </>
  );
};
